int copy(const char* source, const char* destination) {
    int input, output;    
    if ((input = open(source, O_RDONLY)) == -1) {
        return -1;
    }
	
    if ((output = creat(destination, 0660)) == -1) {
        close(input);
        return -1;
    }

    //Kernel-space copying for performance
    int result = fcopyfile(input, output, 0, COPYFILE_ALL);

    close(input);
    close(output);

    return result;
}


